        g_tRfWr     : time     := 60 ps;
        g_tSetup    : time     := 50 ps
    );
    PORT( 
        addr1        : IN     std_ulogic_vector (4 DOWNTO 0);
        addr2        : IN     std_ulogic_vector (4 DOWNTO 0);
