        test     : OUT    std_ulogic_vector (32+3+2+2+1+1+1+2+1+1+2 - 1 DOWNTO 0)
    );
    END COMPONENT;
    COMPONENT DFF
    GENERIC (
        delay : time := gateDelay
    );
    PORT (
        CLK : IN     std_uLogic ;
        CLR : IN     std_uLogic ;
        D   : IN     std_uLogic ;
        Q   : OUT    std_uLogic 
    );
    END COMPONENT;
    COMPONENT TFF
    GENERIC (
        delay : time := gateDelay
    );
    PORT (
        CLK : IN     std_uLogic ;
        CLR : IN     std_uLogic ;
        T   : IN     std_uLogic ;
        Q   : OUT    std_uLogic 
    );
    END COMPONENT;
    COMPONENT freqDivider
    GENERIC (
        divideValue : positive := 256;
        delay       : time     := gateDelay
    );
    PORT (
        clock  : IN     std_ulogic ;
        reset  : IN     std_ulogic ;
        enable : OUT    std_ulogic 
    );
    END COMPONENT;

    -- Optional embedded configurations
    -- pragma synthesis_off
--     FOR ALL : DFF USE ENTITY sequential.DFF;
--     FOR ALL : TFF USE ENTITY sequential.TFF;
--     FOR ALL : bufferUlogic USE ENTITY gates.bufferUlogic;
--     FOR ALL : debouncer USE ENTITY common.debouncer;
--     FOR ALL : edgeDetector USE ENTITY common.edgeDetector;
--     FOR ALL : freqDivider USE ENTITY sequential.freqDivider;
--     FOR ALL : heirv32_mc USE ENTITY HEIRV32_MC.heirv32_mc;
--     FOR ALL : inverter USE ENTITY gates.inverter;
--     FOR ALL : lattice_pll USE ENTITY HEIRV32.lattice_pll;
--     FOR ALL : logic1 USE ENTITY gates.logic1;
--     FOR ALL : or2 USE ENTITY gates.or2;
    -- pragma synthesis_on


BEGIN
    -- Architecture concurrent statements
    -- HDL Embedded Text Block 2 E_IO
    leds <= not lds;
    process(rst, clk_red)
    begin
      if rst='1' then
        bts <= (others => '0');
      elsif rising_edge(clk_red) then
        bts <= not btns;
      end if;
    end process;


    -- Instance port mappings.
    U_fullDbnc : debouncer
        GENERIC MAP (
            counterBitNb => 21,
            invertInput  => '0'
        )
        PORT MAP (
            clock     => clk_red,
            input     => en_full_n,
            reset     => rst_synch,
            debounced => en_full_dbn
        );
    U_stepDbnc : debouncer
        GENERIC MAP (
            counterBitNb => 21,
            invertInput  => '0'
        )
        PORT MAP (
            clock     => clk_red,
            input     => en_step_n,
            reset     => rst_synch,
            debounced => en_step_dbn
        );
    U_fullEdgeDetect : edgeDetector
        PORT MAP (
            clock   => clk_red,
            pulse   => en_full_dbn,
            reset   => rst_synch,
            falling => falling1,
            rising  => OPEN
        );
    U_stepEdgeDetect : edgeDetector
        PORT MAP (
            clock   => clk_red,
            pulse   => en_step_dbn,
            reset   => rst_synch,
            falling => step,
            rising  => OPEN
        );
    U_clkBuffer : bufferUlogic
        GENERIC MAP (
            delay => gateDelay
        )
        PORT MAP (
            in1  => clk_red,
            out1 => LED_G
        );
    U_enInverter : inverter
        GENERIC MAP (
            delay => gateDelay
        )
        PORT MAP (
            in1  => sys_en_internal,
            out1 => LED_R
        );
    U_rstDFFInverter : inverter
        GENERIC MAP (
            delay => gateDelay
        )
        PORT MAP (
            in1  => resetSynch_n,
            out1 => rst_synch
        );
    U_rstInverter : inverter
        GENERIC MAP (
            delay => gateDelay
        )
        PORT MAP (
            in1  => rst_n,
            out1 => rst
        );
    U_logic1 : logic1
        PORT MAP (
            logic_1 => D
        );
    U_enOr : or2
        GENERIC MAP (
            delay => gateDelay
        )
        PORT MAP (
            in1  => step,
            in2  => full,
            out1 => sys_en_internal
        );
    U_pll : lattice_pll
        PORT MAP (
            clkIn  => clk,
            clk50M => clk_red,
            clk10M => OPEN
        );
    U_heirv32 : heirv32_mc
        GENERIC MAP (
            g_programFile => c_programFile,
            g_btnsNb      => c_btnsNb,
            g_dataWidth   => c_dataWidth
        )
        PORT MAP (
            btns     => bts,
            clk      => clk_red,
            en       => sys_en_internal,
            rst      => rst_synch,
            dbg_leds => dbg_leds,
            leds     => lds,
            test     => OPEN
        );
    U_rstDFF : DFF
        GENERIC MAP (
            delay => gateDelay
        )
        PORT MAP (
            CLK => clk,
            CLR => rst,
            D   => D,
            Q   => resetSynch_n
        );
    U_blinkerDFF : TFF
        GENERIC MAP (
            delay => gateDelay
        )
        PORT MAP (
            CLK => clk_red,
            CLR => rst_synch,
            T   => T,
            Q   => heartbeat
        );
    U_fullT : TFF
        GENERIC MAP (
            delay => gateDelay
        )
        PORT MAP (
            CLK => clk_red,
            CLR => rst_synch,
            T   => falling1,
            Q   => full
        );
    U_blinker : freqDivider
        GENERIC MAP (
            divideValue => c_clockFrequencyHz,
            delay       => gateDelay
        )
        PORT MAP (
            clock  => clk_red,
            reset  => rst_synch,
            enable => T
        );

    -- Implicit buffered output assignments
    sys_en <= sys_en_internal;

END struct;




